&lt;form&gt;
  &lt;fieldset&gt;
    &lt;legend&gt;Legend&lt;/legend&gt;
    &lt;div class="row"&gt;
      &lt;label for="staticEmail" class="col-sm-2 col-form-label"&gt;Email&lt;/label&gt;
      &lt;div class="col-sm-10"&gt;
        &lt;input type="text" readonly="" class="form-control-plaintext" id="staticEmail" value="email@example.com"&gt;
      &lt;/div&gt;
    &lt;/div&gt;
    &lt;div&gt;
      &lt;label for="exampleInputEmail1" class="form-label mt-4"&gt;Email address&lt;/label&gt;
      &lt;input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email"&gt;
      &lt;small id="emailHelp" class="form-text text-muted"&gt;We'll never share your email with anyone else.&lt;/small&gt;
    &lt;/div&gt;
    &lt;div&gt;
      &lt;label for="exampleInputPassword1" class="form-label mt-4"&gt;Password&lt;/label&gt;
      &lt;input type="password" class="form-control" id="exampleInputPassword1" placeholder="Password" autocomplete="off"&gt;
    &lt;/div&gt;
    &lt;div&gt;
      &lt;label for="exampleSelect1" class="form-label mt-4"&gt;Example select&lt;/label&gt;
      &lt;select class="form-select" id="exampleSelect1"&gt;
        &lt;option&gt;1&lt;/option&gt;
        &lt;option&gt;2&lt;/option&gt;
        &lt;option&gt;3&lt;/option&gt;
        &lt;option&gt;4&lt;/option&gt;
        &lt;option&gt;5&lt;/option&gt;
      &lt;/select&gt;
    &lt;/div&gt;
    &lt;div&gt;
      &lt;label for="exampleSelect1" class="form-label mt-4"&gt;Example disabled select&lt;/label&gt;
      &lt;select class="form-select" id="exampleDisabledSelect1" disabled=""&gt;
        &lt;option&gt;1&lt;/option&gt;
        &lt;option&gt;2&lt;/option&gt;
        &lt;option&gt;3&lt;/option&gt;
        &lt;option&gt;4&lt;/option&gt;
        &lt;option&gt;5&lt;/option&gt;
      &lt;/select&gt;
    &lt;/div&gt;
    &lt;div&gt;
      &lt;label for="exampleSelect2" class="form-label mt-4"&gt;Example multiple select&lt;/label&gt;
      &lt;select multiple="" class="form-select" id="exampleSelect2"&gt;
        &lt;option&gt;1&lt;/option&gt;
        &lt;option&gt;2&lt;/option&gt;
        &lt;option&gt;3&lt;/option&gt;
        &lt;option&gt;4&lt;/option&gt;
        &lt;option&gt;5&lt;/option&gt;
      &lt;/select&gt;
    &lt;/div&gt;
    &lt;div&gt;
      &lt;label for="exampleTextarea" class="form-label mt-4"&gt;Example textarea&lt;/label&gt;
      &lt;textarea class="form-control" id="exampleTextarea" rows="3"&gt;&lt;/textarea&gt;
    &lt;/div&gt;
    &lt;div&gt;
      &lt;label for="formFile" class="form-label mt-4"&gt;Default file input example&lt;/label&gt;
      &lt;input class="form-control" type="file" id="formFile"&gt;
    &lt;/div&gt;
    &lt;fieldset&gt;
      &lt;legend class="mt-4"&gt;Radio buttons&lt;/legend&gt;
      &lt;div class="form-check"&gt;
        &lt;input class="form-check-input" type="radio" name="optionsRadios" id="optionsRadios1" value="option1" checked=""&gt;
        &lt;label class="form-check-label" for="optionsRadios1"&gt;
          Option one is this and that—be sure to include why it's great
        &lt;/label&gt;
      &lt;/div&gt;
      &lt;div class="form-check"&gt;
        &lt;input class="form-check-input" type="radio" name="optionsRadios" id="optionsRadios2" value="option2"&gt;
        &lt;label class="form-check-label" for="optionsRadios2"&gt;
          Option two can be something else and selecting it will deselect option one
        &lt;/label&gt;
      &lt;/div&gt;
      &lt;div class="form-check disabled"&gt;
        &lt;input class="form-check-input" type="radio" name="optionsRadios" id="optionsRadios3" value="option3" disabled=""&gt;
        &lt;label class="form-check-label" for="optionsRadios3"&gt;
          Option three is disabled
        &lt;/label&gt;
      &lt;/div&gt;
    &lt;/fieldset&gt;
    &lt;fieldset&gt;
      &lt;legend class="mt-4"&gt;Checkboxes&lt;/legend&gt;
      &lt;div class="form-check"&gt;
        &lt;input class="form-check-input" type="checkbox" value="" id="flexCheckDefault"&gt;
        &lt;label class="form-check-label" for="flexCheckDefault"&gt;
          Default checkbox
        &lt;/label&gt;
      &lt;/div&gt;
      &lt;div class="form-check"&gt;
        &lt;input class="form-check-input" type="checkbox" value="" id="flexCheckChecked" checked=""&gt;
        &lt;label class="form-check-label" for="flexCheckChecked"&gt;
          Checked checkbox
        &lt;/label&gt;
      &lt;/div&gt;
    &lt;/fieldset&gt;
    &lt;fieldset&gt;
      &lt;legend class="mt-4"&gt;Switches&lt;/legend&gt;
      &lt;div class="form-check form-switch"&gt;
        &lt;input class="form-check-input" type="checkbox" id="flexSwitchCheckDefault"&gt;
        &lt;label class="form-check-label" for="flexSwitchCheckDefault"&gt;Default switch checkbox input&lt;/label&gt;
      &lt;/div&gt;
      &lt;div class="form-check form-switch"&gt;
        &lt;input class="form-check-input" type="checkbox" id="flexSwitchCheckChecked" checked=""&gt;
        &lt;label class="form-check-label" for="flexSwitchCheckChecked"&gt;Checked switch checkbox input&lt;/label&gt;
      &lt;/div&gt;
    &lt;/fieldset&gt;
    &lt;fieldset&gt;
      &lt;legend class="mt-4"&gt;Ranges&lt;/legend&gt;
        &lt;label for="customRange1" class="form-label"&gt;Example range&lt;/label&gt;
        &lt;input type="range" class="form-range" id="customRange1"&gt;
        &lt;label for="disabledRange" class="form-label"&gt;Disabled range&lt;/label&gt;
        &lt;input type="range" class="form-range" id="disabledRange" disabled=""&gt;
        &lt;label for="customRange3" class="form-label"&gt;Example range&lt;/label&gt;
        &lt;input type="range" class="form-range" min="0" max="5" step="0.5" id="customRange3"&gt;
    &lt;/fieldset&gt;
    &lt;button type="submit" class="btn btn-primary"&gt;Submit&lt;/button&gt;
  &lt;/fieldset&gt;
