library(DiagrammeR)
library(htmlwidgets)

# Define the flowchart using the DOT language
flowchart_code &lt;- "
digraph flowchart {
  node [shape = box, style = filled, fillcolor = lightblue];

  A [label = 'Start'];
  B [label = 'Process Data'];
  C [label = 'Make Decision'];
  D [label = 'Option 1'];
  E [label = 'Option 2'];
  F [label = 'End'];

  A -&gt; B;
  B -&gt; C;
  C -&gt; D [label = 'Yes'];
  C -&gt; E [label = 'No'];
  D -&gt; F;
  E -&gt; F;
}
"

# Create the flowchart object
flowchart &lt;- grViz(flowchart_code)

# Display the interactive flowchart
flowchart




# Save the flowchart as an HTML file (optional)
# saveWidget(flowchart, "flowchart.html", selfcontained = TRUE)

# Example with more interactive features (tooltips, etc.)
flowchart_code_interactive &lt;- "
digraph flowchart {
  node [shape = box, style = filled, fillcolor = lightblue];

  A [label = 'Start', tooltip = 'Begin the process'];
  B [label = 'Process Data', tooltip = 'Perform data manipulation'];
  C [label = 'Make Decision', tooltip = 'Evaluate conditions'];
  D [label = 'Option 1', tooltip = 'Execute if condition is true'];
  E [label = 'Option 2', tooltip = 'Execute if condition is false'];
  F [label = 'End', tooltip = 'Terminate the process'];

  A -&gt; B;
  B -&gt; C;
  C -&gt; D [label = 'Yes', tooltip = 'Condition satisfied'];
  C -&gt; E [label = 'No', tooltip = 'Condition not satisfied'];
  D -&gt; F;
  E -&gt; F;
}
"

flowchart_interactive &lt;- grViz(flowchart_code_interactive)
flowchart_interactive




#Example with specific node attributes.

flowchart_code_attrib &lt;- "
digraph flowchart {
  node [shape = box, style = filled, fillcolor = lightblue];

  A [label = 'Start', tooltip = 'Begin process', fillcolor = 'green'];
  B [label = 'Process Data', tooltip = 'Data manipulation', shape = 'oval'];
  C [label = 'Decision', tooltip = 'Evaluate', shape='diamond', fillcolor = 'yellow'];
  D [label = 'Option 1', tooltip = 'True branch'];
  E [label = 'Option 2', tooltip = 'False branch'];
  F [label = 'End', tooltip = 'Finish process', fillcolor = 'red'];

  A -&gt; B;
  B -&gt; C;
  C -&gt; D [label = 'Yes'];
  C -&gt; E [label = 'No'];
  D -&gt; F;
  E -&gt; F;
}
"

flowchart_attrib &lt;- grViz(flowchart_code_attrib)
flowchart_attrib
